HtmlCardTexts[CardNames.BUTCHER]='<div class="card-text" style="top:5px;"><div style="position:relative; top:undefinedpx;"><div style="font-weight: bold;"><div style="line-height:28px;"><div style="display:inline;"><div style="display:inline; font-size:28px;">+2 Coffres</div></div><br></div></div></div><div style="position:relative; top:2px;"><div style="line-height:18px;"><div style="display:inline;"><div style="display:inline; font-size:18px;">Vous pouvez écarter une carte de votre</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">main. Dans ce cas, dépensez autant</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">de jetons de vos Coffres que souhaité,</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">et recevez une carte coûtant au plus</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">le coût de la carte écartée plus</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">le nombre de jetons dépensés.</div></div><br></div></div></div>'
HtmlCardTexts[CardNames.CANDLESTICKMAKER]='<div class="card-text" style="top:47px;"><div style="position:relative; top:0px;"><div style="font-weight: bold;"><div style="line-height:28px;"><div style="display:inline;"><div style="display:inline; font-size:28px;">+1 Action</div></div><br><div style="display:inline;"><div style="display:inline; font-size:28px;">+1 Achat</div></div><br><div style="display:inline;"><div style="display:inline; font-size:28px;">+1 Coffres</div></div><br></div></div></div></div>'
HtmlCardTexts[CardNames.DOCTOR]='<div class="card-text" style="top:2px;"><div style="position:relative; top:5px;"><div style="line-height:16px;"><div style="display:inline;"><div style="display:inline; font-size:16px;">Nommez une carte. Dévoilez les 3</div></div><br><div style="display:inline;"><div style="display:inline; font-size:16px;">premières cartes de votre pioche. Écartez</div></div><br><div style="display:inline;"><div style="display:inline; font-size:16px;">celles dont le nom correspond, et remettez</div></div><br><div style="display:inline;"><div style="display:inline; font-size:16px;">les autres sur votre pioche.</div></div><br></div></div><div class="horizontal-line" style="width:200px; height:3px;margin-top:8px;"></div><div style="position:relative; top:-2px;"><div style="line-height:15px;"><div style="display:inline;"><div style="display:inline; font-size:16px;">Quand vous achetez cette carte, vous</div></div><br><div style="display:inline;"><div style="display:inline; font-size:16px;"> pouvez surpayer. Pour chaque       surpayé,</div></div><br><div style="display:inline;"><div style="display:inline; font-size:16px;">consultez la carte du haut de votre pioche;</div></div><br><div style="display:inline;"><div style="display:inline; font-size:16px;">écartez-la, défaussez-la, ou replacez-la.</div></div><br></div></div><div class="card-text-coin-icon" style="transform:scale(0.16); top:115px; display: inline;left:200px;"><div class="card-text-coin-text-container" style="display:inline;"><div class="card-text-coin-text" style="color: black; display:inline; top:8px;"></div></div></div></div>'
HtmlCardTexts[CardNames.HERALD]='<div class="card-text" style="top:2px;"><div style="position:relative; top:-2px;"><div style="font-weight: bold;"><div style="line-height:21px;"><div style="display:inline;"><div style="display:inline; font-size:21px;">+1 Carte</div></div><br><div style="display:inline;"><div style="display:inline; font-size:21px;">+1 Action</div></div><br></div></div></div><div style="position:relative; top:-5px;"><div style="line-height: 17px;"><div style="display:inline;"><div style="display:inline; font-size:18px;">Dévoilez la carte du haut de votre</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">pioche. Si c\'est une Action, jouez-la.</div></div><br></div></div><div class="horizontal-line" style="width:200px; height:3px;margin-top:-2px;"></div><div style="position:relative;top: -3px;"><div style="line-height: 14px;"><div style="display:inline;"><div style="display:inline;font-size: 17px;">Quand vous achetez cette carte, vous</div></div><br><div style="display:inline;"><div style="display:inline;font-size: 17px;">pouvez surpayer. Pour chaque       </div></div><br><div style="display:inline;"><div style="display:inline;font-size: 17px;">surpayé, consultez votre défausse et</div></div><br><div style="display:inline;"><div style="display:inline;font-size: 17px;">placez-en une carte sur votre pioche.</div></div><br></div></div><div class="card-text-coin-icon" style="transform:scale(0.19);top: 110px;display: inline;left: 235px;"><div class="card-text-coin-text-container" style="display:inline;"><div class="card-text-coin-text" style="color: black; display:inline; top:8px;"></div></div></div></div>'
HtmlCardTexts[CardNames.JOURNEYMAN]='<div class="card-text" style="top:20px;"><div style="position:relative; top:10px;"><div style="line-height:19.5px;"><div style="display:inline;"><div style="display:inline; font-size:19.5px;">Nommez une carte. Dévoilez des</div></div><br><div style="display:inline;"><div style="display:inline; font-size:19.5px;">cartes de votre pioche jusqu\'à</div></div><br><div style="display:inline;"><div style="display:inline; font-size:19.5px;">dévoiler 3 cartes dont le nom</div></div><br><div style="display:inline;"><div style="display:inline; font-size:19.5px;">ne correspond pas. Prenez-les</div></div><br><div style="display:inline;"><div style="display:inline; font-size:19.5px;">en main et défaussez le reste.</div></div><br></div></div></div>'
