HtmlCardTexts[CardNames.STONEMASON]='<div class="card-text" style="top:10px;"><div style="position:relative; top:8px;"><div style="line-height:18.5px;"><div style="display:inline;"><div style="display:inline; font-size:18.5px;">Écartez une carte de votre main.</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18.5px;">Recevez 2 cartes de coût inférieur.</div></div><br></div></div><div class="horizontal-line" style="width:200px; height:3px;margin-top:18px;"></div><div style="position:relative; top:3px;"><div style="line-height:18.5px;"><div style="display:inline;"><div style="display:inline; font-size:18.5px;">Quand vous achetez cette carte, vous</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18.5px;">pouvez surpayer. Dans cas, recevez</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18.5px;">2 cartes Action coûtant exactement</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18.5px;">le montant surpayé.</div></div><br></div></div></div>'
HtmlCardTexts[CardNames.TAXMAN]='<div class="card-text" style="top:5px;"><div style="position:relative; top:5px;"><div style="line-height:18px;"><div style="display:inline;"><div style="display:inline; font-size:18px;">Vous pouvez écarter une carte</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">Trésor de votre main. Tous vos</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">adversaires ayant au moins 5 cartes</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">en main en défaussent un exemplaire</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">(à défaut, dévoilent leur main).</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">Recevez sur votre pioche une carte</div></div><br><div style="display:inline;"><div style="display:inline; font-size:18px;">Trésor coûtant jusqu\'à       de plus.</div></div><br></div></div><div class="card-text-coin-icon" style="transform:scale(0.19); top:136px; display: inline;left:180px;"><div class="card-text-coin-text-container" style="display:inline;"><div class="card-text-coin-text" style="color: black; display:inline; top:8px;">3</div></div></div></div>'
